mapper.select:
SELECT COUNT(*) 
FROM reply 
WHERE re_parent_no = #{reParentNo} 
AND re_category = #{reCategory}


SELECT * FROM ( SELECT a.*, ROWNUM AS rnum FROM ( SELECT re_no, re_category, re_parent_no, re_mem_id, 
(SELECT mem_name FROM member 
WHERE re_mem_id = mem_id) AS re_mem_name, re_content, re_ip, 
TO_CHAR(re_reg_date, 'YYYY/MM/DD HH24:MI') AS re_reg_date,
TO_CHAR(re_mod_date, 'YYYY/MM/DD HH24:MI') AS re_mod_date 
FROM reply 
WHERE re_parent_no = #{reParentNo} 
AND re_category = #{reCategory} 
ORDER BY re_no 
) a ) b 
WHERE rnum BETWEEN #{firstRow} AND #{lastRow}
