mapper.insert:
INSERT INTO attach ( atch_no , atch_parent_no , atch_category , atch_file_name , atch_original_name , atch_file_size , atch_fancy_size , atch_content_type , atch_path , atch_down_hit , atch_del_yn , atch_reg_date ) VALUES (
seq_attach.NEXTVAL, #{atchParentNo}, #{atchCategory}, #{atchFileName}, #{atchOriginalName}, #{atchFileSize}, #{atchFancySize}, #{atchContentType}, #{atchPath}, 0, 'N', SYSDATE
)

mapper.update:
DELETE attach
 WHERE 1 = 1 /* 의미없는 라인, 배열이 null이거나 length가 0개일때 삭제 안되게 */

 	#{tj}

UPDATE attach
   SET atch_down_hit = atch_down_hit + 1
 WHERE atch_no = #{atchNo}
